import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [usuario, setUsuario] = useState('');
    const [contrasena, setContrasena] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.get('http://localhost:3001/login/usuarios', {
                params: { Nombre: usuario, Contrasena: contrasena }
            });

            if (response.data.length > 0) {
                // Aquí puedes manejar el inicio de sesión exitoso,
                // por ejemplo, guardar la información del usuario en el estado o en el almacenamiento local.
                console.log('Inicio de sesión exitoso');
            } else {
                console.log('Nombre de usuario o contraseña incorrectos');
            }
        } catch (error) {
            console.error('Error al realizar el inicio de sesión:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Nombre de usuario:
                <input type="text" value={usuario} onChange={e => setUsuario(e.target.value)} />
            </label>
            <label>
                Contraseña:
                <input type="password" value={contrasena} onChange={e => setContrasena(e.target.value)} />
            </label>
            <button type="submit">Iniciar sesión</button>
        </form>
    );
};

export default Login;
